// Parse file list and filter empty
const stdout = $input.first().json.stdout || '';
const files = stdout.trim().split('\n').filter(f => f.length > 0);

if (files.length === 0) {
  return []; // No files to process
}

return files.map(filePath => ({
  json: { filePath, fileName: filePath.split('/').pop() }
}));

const content = $input.first().json.stdout || '';
const filePath = $('Process Each File').first().json.filePath;
const fileName = $('Process Each File').first().json.fileName;

// Parse YAML frontmatter if present
let frontmatter = {};
let body = content;

const fmMatch = content.match(/^---\n([\s\S]*?)\n---\n/);
if (fmMatch) {
  const fmText = fmMatch[1];
  body = content.slice(fmMatch[0].length);

  // Simple YAML parsing for common fields
  fmText.split('\n').forEach(line => {
    const match = line.match(/^(\w+):\s*(.*)$/);
    if (match) {
      let value = match[2].trim();
      // Handle arrays
      if (value.startsWith('[') && value.endsWith(']')) {
        value = value.slice(1, -1).split(',').map(s => s.trim().replace(/["']/g, ''));
      }
      // Remove quotes
      if ((value.startsWith('"') && value.endsWith('"')) || (value.startsWith("'") && value.endsWith("'"))) {
        value = value.slice(1, -1);
      }
      frontmatter[match[1]] = value;
    }
  });
}

return [{
  json: {
    filePath,
    fileName,
    frontmatter,
    body,
    hasAbout: !!frontmatter.about,
    originalContent: content
  }
}];

const taxonomy = $('Read Taxonomy').first().json.stdout;
const noteData = $('Parse Frontmatter').first().json;

const prompt = `You are an Obsidian vault organizer. Given a note and the vault's folder taxonomy, determine where this note belongs.

## Folder Taxonomy
${taxonomy}

## Note to Categorize
Filename: ${noteData.fileName}

Existing frontmatter: ${JSON.stringify(noteData.frontmatter)}

Content:
${noteData.body.slice(0, 4000)}

## Instructions
1. Choose the best folder path from the taxonomy above
2. Be VERY conservative about suggesting new second-level folders - only if nothing existing fits at all
3. You may suggest new third-level folders if appropriate
4. Generate a brief summary (1 sentence) if no 'about' field exists
5. Suggest relevant tags (2-5 tags)

Respond in JSON format only:
{
  "destination": "/full/path/to/folder",
  "confidence": "high" | "medium" | "low",
  "summary": "one sentence summary (omit if note has 'about' field)",
  "tags": ["tag1", "tag2"],
  "reasoning": "brief explanation of choice"
}`;

return [{ json: { ...noteData, prompt } }];

const response = $input.first().json;
const noteData = $('Parse Frontmatter').first().json;

try {
  const content = response.content[0].text;
  // Extract JSON from response (handle markdown code blocks)
  let jsonStr = content;
  const jsonMatch = content.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  }

  const result = JSON.parse(jsonStr.trim());

  return [{
    json: {
      ...noteData,
      destination: result.destination,
      confidence: result.confidence,
      summary: result.summary,
      tags: result.tags,
      reasoning: result.reasoning,
      success: true
    }
  }];
} catch (error) {
  return [{
    json: {
      ...noteData,
      success: false,
      error: error.message
    }
  }];
}

const data = $input.first().json;
const vaultBase = '/home/joshsymonds/obsidian-vault/chancel';

// Build new frontmatter
const newFm = { ...data.frontmatter };

// Add/update fields
if (!data.hasAbout && data.summary) {
  newFm.summary = data.summary;
}
if (data.tags && data.tags.length > 0) {
  newFm.tags = data.tags;
}
newFm.categorized_at = new Date().toISOString().split('T')[0];

// Build frontmatter string
let fmLines = ['---'];
for (const [key, value] of Object.entries(newFm)) {
  if (Array.isArray(value)) {
    fmLines.push(`${key}: [${value.map(v => `"${v}"`).join(', ')}]`);
  } else if (typeof value === 'string' && (value.includes(':') || value.includes('#'))) {
    fmLines.push(`${key}: "${value}"`);
  } else {
    fmLines.push(`${key}: ${value}`);
  }
}
fmLines.push('---', '');

const newContent = fmLines.join('\n') + data.body;

// Ensure destination exists and build full path
let destFolder = data.destination;
if (!destFolder.startsWith(vaultBase)) {
  destFolder = vaultBase + destFolder;
}
const destPath = destFolder + '/' + data.fileName;

// Base64 encode content for safe shell passing
const contentBase64 = Buffer.from(newContent).toString('base64');

return [{
  json: {
    ...data,
    newContent,
    contentBase64,
    destFolder,
    destPath
  }
}];

const data = $input.first().json;
const vaultBase = '/home/joshsymonds/obsidian-vault/chancel';
const deadLetters = vaultBase + '/Inbox/Dead Letters';

// Build error frontmatter
const newFm = { ...data.frontmatter };
newFm.categorization_error = data.error || 'Low confidence: ' + (data.reasoning || 'unknown');
newFm.error_at = new Date().toISOString();

// Build frontmatter string
let fmLines = ['---'];
for (const [key, value] of Object.entries(newFm)) {
  if (Array.isArray(value)) {
    fmLines.push(`${key}: [${value.map(v => `"${v}"`).join(', ')}]`);
  } else if (typeof value === 'string' && (value.includes(':') || value.includes('#') || value.includes('"'))) {
    fmLines.push(`${key}: "${value.replace(/"/g, '\\"')}"`);
  } else {
    fmLines.push(`${key}: ${value}`);
  }
}
fmLines.push('---', '');

const newContent = fmLines.join('\n') + data.body;
const destPath = deadLetters + '/' + data.fileName;

// Base64 encode content for safe shell passing
const contentBase64 = Buffer.from(newContent).toString('base64');

return [{
  json: {
    ...data,
    newContent,
    contentBase64,
    destPath,
    deadLetters
  }
}];
